import React from 'react'
import { useMachine } from '@xstate/react'
import {myMachine} from "../Utility/myFirstMachine"

export const Card = () => {
  const [state, send] = useMachine(myMachine);
  // state.value                -> current state
  // send({ type: TRANSITION }) -> transition between

  return (
    <div>
      {JSON.stringify(state.value)}
      <button
        onClick = {() => {send({ type: "MOUSEOVER" });}}>
          MOUSEOVER
      </button>
      <button
        onClick = {() => {send({ type: "MOUSEOUT" });}}>
          MOUSEOUT
      </button>
    </div>
  )
}
