export const fetchMachine = createMachine({
    /** @xstate-layout -IFglAHbSVQgjidSk0yj7tO7c-79IHgzJRT71I5WoZrb4BsNRsnlWDcvpBed7DJtFJblZETVdvU7qaZNIS1JdtXnm8PrAvj8G6D6Yg8icrO19Lr1z7NLyUXY0bs4dsuzJPJ4gA */
    id: 'fetch',
    initial: 'idle',
    context: {
        results: undefined,
        message: undefined
    },

    states:{
        idle: {
            on: {FETCH: 'pending'} 
        },

        pending: {
            entry: ['fetchData'],
            on: {
                RESOLVE: {target: 'successful', actions: ['setResults']}
            }
        },

        failed: {
            on: {FETCH: 'pending'}
        },

        successful: {
            on: {FETCH: 'pending'}
        }
    },  
}, {
    actions: {
        setResults: assign((context, event) => ({
            results: event.results
        })),

        setMessage: assign((context, event) => ({
            message: event.message
        })),

        fetchData: (context, event) => {
            const URL = "https://api.thecatapi.com/v1/images/search";
            const LIMIT = "10";
            const KEY = "live_3H0P5gLpYlRVspXYdZgndGhjKVNAnTR2Mv634ZpCjkAbdlW75qNWsgCKz7SaeJ9B";
            const HAS_BREEDS = "1";
            const apiLink = `${URL}?limit=${LIMIT}&has_breeds=${HAS_BREEDS}&api_key=${KEY}`;
            const requestOptions = {
                method: "GET",
                redirect: "follow"
            };
            fetch(apiLink, requestOptions).then(
                response => response.json()
              ).then(
                data => data.map(catObject)
              ).then(
                (content) =>{
                    console.log(content);
                    return content;
                    // setData(JSON.stringify(content));
              }
    );
        }


    }

})
