import React from 'react'
import {useEffect} from "react"
import { useMachine } from '@xstate/react'
import { fetchMachine } from "../Utility/myFirstMachine"
import { catObject } from '../Utility/catObject'


export const Card = () => {
  const [fetchState, sendToFetchMachine] = useMachine(fetchMachine, {
    actions: {
      fetchData: async (context, event) => {
        const res = await fetch(apiLink, requestOptions);
        const data = await res.json();
        let dataDisplay = data.map(catObject);
        console.log(dataDisplay);
        return dataDisplay;
      }
    }
  });
  // state.value                -> current state
  // send({ type: TRANSITION }) -> transition between states

  const URL = "https://api.thecatapi.com/v1/images/search";
  const LIMIT = "10";
  const KEY = "live_3H0P5gLpYlRVspXYdZgndGhjKVNAnTR2Mv634ZpCjkAbdlW75qNWsgCKz7SaeJ9B";
  const HAS_BREEDS = "1";
  const apiLink = `${URL}?limit=${LIMIT}&has_breeds=${HAS_BREEDS}&api_key=${KEY}`;
  const requestOptions = {
    method: "GET",
    redirect: "follow"
  };

  
  // API CALL
  useEffect(() => {
    // use
    console.log("Test")
    async function fetchCats(){
      const payload = await getData();
      let dataDisplay = payload.map(catObject);
      console.log(dataDisplay);
    }
    // fetchCats();
  });

  

  const getData = async () => {
    try{
      const res = await fetch(apiLink, requestOptions);
      const data = await res.json();
      let dataDisplay = data.map(catObject);
      console.log(dataDisplay);
      return dataDisplay;
    } catch (err) {
      console.log(err)
    }
  }

  return (
    <div>
      {JSON.stringify(fetchState.value)}
      <button
        onClick = {() => {sendToFetchMachine({ type: "MOUSEOVER" });}}>
          MOUSEOVER
      </button>
      <button
        onClick = {() => {sendToFetchMachine({ type: "MOUSEOUT" });}}>
          MOUSEOUT
      </button>
    </div>
  )
}
