import React from 'react'
// import {useEffect} from "react"
import { useMachine } from '@xstate/react'
import { fetchMachine } from "../Utility/myFirstMachine"
import { catObject } from '../Utility/catObject'


export const Card = () => {
  const [fetchState, sendToFetchMachine] = useMachine(fetchMachine, {
    actions: {
      fetchData: async (context, event) => {
        await fetch(apiLink, requestOptions)
          .then(
            response => response.json()
          ).then((data) => {
            data.map(catObject);
            co
          }
          ).then(
            content => {sendToFetchMachine({ type: 'RESOLVE', results: content })}, 
            message => {sendToFetchMachine({ type: "REJECT", message})}
          );
      }
    }
  });
  // state.value                -> current state
  // send({ type: TRANSITION }) -> transition between states

  const URL = "https://api.thecatapi.com/v1/images/search";
  const LIMIT = "10";
  const KEY = "live_3H0P5gLpYlRVspXYdZgndGhjKVNAnTR2Mv634ZpCjkAbdlW75qNWsgCKz7SaeJ9B";
  const HAS_BREEDS = "1";
  const apiLink = `${URL}?limit=${LIMIT}&has_breeds=${HAS_BREEDS}&api_key=${KEY}`;
  const requestOptions = {
    method: "GET",
    redirect: "follow"
  };

  
  // API CALL
  // useEffect(() => {
  //   console.log("useEffect -> fetchCats")
  //   getData();
  // });

  

  // const getData = async () => {
  //   await fetch(apiLink, requestOptions).then(
  //     response => response.json()
  //   ).then(
  //     data => data.map(catObject)
  //   ).then((content) =>{
  //     console.log(content);
  //     return content;
  //   });
  // }

  return (
    <div>
      {JSON.stringify(fetchState.value)}
      <button
        onClick = {() => {sendToFetchMachine({ type: "FETCH" });}}>
          Fetch
      </button>
      {fetchState.matches('pending') ? <p>Loading</p> : null}
      {fetchState.matches('successful') ? (
        <ul>
          {fetchState.context.results && fetchState.context.results.map((object) => <p>{object}</p> )}
        </ul>
      ) : null}
      {fetchState.matches('failed') ? <p>Failed</p> : null}

      
    </div>
  )
}
